mapper.insert:
    update CIP_ORGANIZATION 
	set NAME = #{NAME} 			
		, TELEPHONE = #{TELEPHONE} 			
		, ORDER_NUMBER = #{ORDER_NUMBER} 			
		, PARENT_ORGANIZATION_SEQ = null 			
	where ORGANIZATION_SEQ = #{ORGANIZATION_SEQ}

    INSERT INTO CIP_ORGANIZATION (
		ORGANIZATION_SEQ			
		, NAME			
		, TELEPHONE			
		, ORDER_NUMBER			
		, PARENT_ORGANIZATION_SEQ			
	)
	values(
		#{ORGANIZATION_SEQ}			
		, #{NAME}			
		, #{TELEPHONE}			
		, #{ORDER_NUMBER}			
		, null			
	)
